var Canvas = require('./');

var pi = Math.PI;
var pi2 = pi/2;
var sin = Math.sin;
var cos = Math.cos;

var canvas = new Canvas(200, 40);
var c = canvas.getContext('2d');

c.beginPath();
c.moveTo(90, 30);
c.lineTo(90, 10);
c.lineTo(110, 10);
c.lineTo(110, 30);
c.lineTo(90, 30);
c.stroke();

c.beginPath();
c.moveTo(100, 20);
c.arc(100, 20, 10, 0, pi, true);
c.fill();

console.log(c.toString());
